mapper.update:
UPDATE notice
SET
notice_no = #{noticeNo}
	, service_name = #{serviceName}
	, notice_title = #{noticeTitle} 
	, notice_memo = #{noticeMemo} 
	, updatedate = CURDATE()
WHERE notice_no = #{noticeNo}

mapper.insert:
INSERT INTO notice (
	service_name 
	, notice_title 
	, notice_memo 
	, createdate
) VALUES (
	#{serviceName}
	,#{noticeTitle}
	,#{noticeMemo}
	, CURDATE()
)
